import { useRef, useState } from "react";
import "./styles.css";
import { useDrag } from "./useDrag";

export default function App() {
  const [boxes, setBoxes] = useState([
    { id: 1, left: 10, top: 10 },
    { id: 2, left: 10, top: 70 },
    { id: 3, left: 10, top: 130 },
  ]);

  const draggableRef = useRef(null);

  // create five positions for the draggable element
  const positions = [];
  for (let i = 0; i < 5; i++) {
    const { position, handleMouseDown } = useDrag({
      ref: draggableRef
    });
    positions.push({
      position,
    });
  }
  return (
    <div className="App">
      {boxes.map((box) => (
        <div
          className="draggable"
          onMouseDown={handleMouseDown}
          ref={draggableRef}
          style={{
            top: position.y,
            left: position.x
          }}
        >
        </div>
      ))}
    </div>
  );
}
