import { useEffect, useRef, useState } from "react";
import "./styles.css";
import { useDrag } from "./useDrag";

export default function App() {

  const positions = [];
  // for (let i = 0; i < 5; i++) {
  //   const draggableRef = useRef(null);
  //   const { position: position, handleMouseDown: handleMouseDown } = useDrag({
  //     ref: draggableRef,
  //     id: i,
  //     visibleArea: visibleArea,
  //     updateArea: callBack,
  //   });
  //   positions.push({ position, handleMouseDown, id: i, ref: draggableRef });
  // }

  // function to calculate the total visible area of the red box
  const calculateVisibleArea = () => {
    const redBox = document.getElementById('red-box');
    return redBox.offsetWidth * redBox.offsetHeight;
  };

  // variable to store the total visible area of the red box
  const [visibleArea, setVisibleArea] = useState(0);

  useEffect(() => {
    setVisibleArea(calculateVisibleArea());
    for (let i = 0; i < 5; i++) {
      const draggableRef = useRef(null);
      const { position: position, handleMouseDown: handleMouseDown } = useDrag({
        ref: draggableRef,
        id: i,
        visibleArea: visibleArea,
        updateArea: callBack,
      });
      positions.push({ position, handleMouseDown, id: i, ref: draggableRef });
    }
  }, []);

  const callBack = (e) => {
    console.log(e);
  }
  // const updateVisibleArea = (e) => {
  //   let all_overlapping_area = 0
  //   positions.forEach((pos) => {
  //     // sum up all the overlapping area 
  //     console.log(pos);
  //     all_overlapping_area += pos.handleMouseMove(e);
  //   });
  //   console.log(all_overlapping_area)
  //   //setVisibleArea(visibleArea - all_overlapping_area);
  // }

  return (
    <div className="App">
      {positions.map((pos) => (
        <div
          key={pos.id}
          className="draggable"
          onMouseDown={pos.handleMouseDown}
          ref={pos.ref}
          style={{
            top: pos.position.y,
            left: pos.position.x
          }}
        >
        </div>
      ))}
      <div className="big-container">
        <div
          className="red-box"
          id="red-box"
        />
        <div className="label">
          Total Visible Area: {visibleArea} pixels
        </div>
      </div>

    </div >
  );
}
