import { useEffect, useRef, useState } from "react";
import "./styles.css";
import { useDrag } from "./useDrag";

export default function App() {


  const positions = [];
  let prev = 0;
  // variable to store the total visible area of the red box
  const [visibleArea, setVisibleArea] = useState(0);

  // variable to store the overlapping area of the red box
  const [current_overlapping_area, setOverlappingArea] = useState(0);

  for (let i = 0; i < 5; i++) {
    const draggableRef = useRef(null);
    const { position: position, handleMouseDown: handleMouseDown } = useDrag({
      ref: draggableRef,
      id: i,
      callBack: function (e) { setVisibleArea(visibleArea - e + prev); current_overlapping_area = e; },
    });
    positions.push({ position, handleMouseDown, id: i, ref: draggableRef });
  }

  // function to calculate the total visible area of the red box
  const calculateVisibleArea = () => {
    const redBox = document.getElementById('red-box');
    return redBox.offsetWidth * redBox.offsetHeight;
  };

  useEffect(() => {
    setVisibleArea(calculateVisibleArea());
  }, []);

  // function to handle the mouse up event
  const handleMouseUp = () => {
    prev = current_overlapping_area;
    console.log(prev)
  }

  return (
    <div className="App">
      {positions.map((pos) => (
        <div
          key={pos.id}
          className="draggable"
          onMouseDown={pos.handleMouseDown}
          onMouseUp={handleMouseUp}
          ref={pos.ref}
          style={{
            top: pos.position.y,
            left: pos.position.x
          }}
        >
        </div>
      ))}
      <div className="big-container">
        <div
          className="red-box"
          id="red-box"
        />
        <div className="label">
          Total Visible Area: {visibleArea} pixels
        </div>
      </div>

    </div >
  );
}
