import { useRef, useState } from "react";
import "./styles.css";
import { useDrag } from "./useDrag";

export default function App() {
  const [boxes, setBoxes] = useState([
    { id: 1, left: 10, top: 10 },
    { id: 2, left: 10, top: 70 },
    { id: 3, left: 10, top: 130 },
  ]);

  // const draggableRef = useRef(null);
  // const draggableRef2 = useRef(null);
  // create 5 draggable refs
  const draggableRefs = useRef([]);
  const positions = [];
  for (let i = 0; i < 5; i++) {
    draggableRefs.current[i] = useRef(null);
    const draggableRef = useRef(null);
    const { position: position, handleMouseDown: handleMouseDown } = useDrag({
      ref: draggableRef
    });
    positions.push({ position, handleMouseDown });
  }
  console.log(draggableRefs.current)

  // create five positions for the draggable element
  // const positions = [];
  // for (let i = 0; i < 5; i++) {
  // const { position: position, handleMouseDown: handleMouseDown } = useDrag({
  //   ref: draggableRef
  // });

  // const { position: position2, handleMouseDown: handleMouseDown2 } = useDrag({
  //   ref: draggableRef2
  // });

  //   positions.push({ position, handleMouseDown });
  // }
  // console.log(positions)
  return (
    <div className="App">
      {positions.map((position) => (
        <div
          className="draggable"
          onMouseDown={position.handleMouseDown}
          ref={draggableRefs.current[0]}
          style={{
            top: position.y,
            left: position.x
          }}
        >
        </div>
      ))}
    </div>
  );
}
