import { useRef, useState } from "react";
import "./styles.css";
import { useDrag } from "./useDrag";

export default function App() {

  const positions = [];
  for (let i = 0; i < 5; i++) {
    const draggableRef = useRef(null);
    const { position: position, handleMouseDown: handleMouseDown } = useDrag({
      ref: draggableRef,
      id: i,
    });
    positions.push({ position, handleMouseDown, id: i, ref: draggableRef });
  }

  return (
    <div className="App">
      {positions.map((pos) => (
        <div
          key={pos.id}
          className="draggable"
          onMouseDown={pos.handleMouseDown}
          ref={pos.ref}
          style={{
            top: pos.position.y,
            left: pos.position.x
          }}
        >
        </div>
      ))}
      <div>
        <div
          className="red-box"
        />
        <div className="label">
          Total Visible Area: 0 pixels
        </div>
      </div>

    </div >
  );
}
