import { useRef, useState } from "react";
import "./styles.css";
import { useDrag } from "./useDrag";

export default function App() {
  const [boxes, setBoxes] = useState([
    { id: 1, left: 342, top: 576 },
    { id: 2, left: 483, top: 500 },
    { id: 3, left: 213, top: 376 },
    { id: 4, left: 145, top: 535 },
    { id: 5, left: 372, top: 311 },
  ]);

  const positions = [];
  for (let i = 0; i < 5; i++) {
    const draggableRef = useRef(null);
    const { position: position, handleMouseDown: handleMouseDown } = useDrag({
      ref: draggableRef
    });
    positions.push({ position, handleMouseDown, id: i, ref: draggableRef });
  }

  return (
    <div className="App">
      {positions.map((pos) => (
        <div
          key={pos.id}
          className="draggable"
          onMouseDown={pos.handleMouseDown}
          ref={pos.ref}
          style={{
            top: pos.position.y,
            left: pos.position.x
          }}
        >
        </div>
      ))}
    </div>
  );
}
