import { useRef } from "react";
import "./styles.css";
import { useDrag } from "./useDrag";

export default function App() {
  const draggableRef = useRef(null);

  const { position, handleMouseDown } = useDrag({
    ref: draggableRef
  });

  return (
    <div className="App">
      <div
        className="draggable"
        onMouseDown={handleMouseDown}
        ref={draggableRef}
        style={{
          top: position.y,
          left: position.x
        }}
      >

      </div>
    </div>
  );
}
